//讀用戶images
function saveWork(wName, wContent, wPic, uNum, uAccount, uName){
  var images= new Array();
  firebase.database().ref('/images').once('value').then(function(snapshot) {
    images=snapshot.val();
    var imgNum = images.length;
    var imgRef=database.ref('images/'+imgNum);
    imgRef.set({
      date: 1,
      base64: wPic,
      uName: uName,
      uAccount: uAccount,
      wName: wName,
      uId: uNum,
      wContent: wContent
    });
  });

  var person= new Array();
  firebase.database().ref('/'+uAccount).once('value').then(function(snapshot) {
    person=snapshot.val();
    var pNum = person.length;
    var pRef=database.ref(uAccount+'/'+pNum);
    pRef.set({
      date: 1,
      base64: wPic,
      uName: uName,
      uAccount: uAccount,
      wName: wName,
      uId: uNum,
      wContent: wContent
    });
  });

  
}

//註冊帳號
function register(num, uAccount, uPwd, uName) {
    var point=0;
    var userRef = database.ref('user/'+num);//num?
    userRef.set({
      uAccount: uAccount,
      uPwd: uPwd,
      uName: uName,
      point: point
    });
    firebase.database().ref('/user').once('value').then(function(snapshot) {
      users=snapshot.val();
    });

    var imgRef = database.ref('/'+uAccount+'/1');
    imgRef.set({
      base64: "data:image/png;base64,",
      wName: "Let's Draw",
      wContent: "Super Cool Painter"
    });
}

//檢查登入
function loginCheck(uAccount, uPwd){
  firebase.database().ref('/user').once('value').then(function(snapshot) {
    users=snapshot.val();
  });
  var notExit=0;
  var exit;
  for (var i=1;i<users.length;i++){
    if (uAccount!=users[i].uAccount){
      notExit++;
    }else{
      exit=i;
    }
  }
  if(notExit==(users.length-1)){
    return 0;
  }else{
    if(uPwd==users[exit].uPwd){
      return exit;
    }else{
      return 0;
    }
  }
}


